const d=document,$feedback_button=d.querySelector(".feedback-button"),$feedback_content=d.querySelector(".feedback-content"),$feedback_message=d.querySelector(".feedback-message"),$feedback_score=d.querySelectorAll(".feedback-score"),handleClick=()=>{$feedback_content.style.display="none",$feedback_message.style.display="flex",setTimeout(()=>{$feedback_message.style.transform="scale(1)"},10)},cleanScore=()=>{$feedback_score.forEach(e=>{e.classList.remove("isSelected")})};$feedback_button.addEventListener("click",handleClick),$feedback_score.forEach((e,c)=>{e.addEventListener("click",()=>{cleanScore();for(let e=0;e<=c;e++)$feedback_score[e].classList.add("isSelected")})});